pragma experimental ABIEncoderV2;

import "./IAsset.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

pragma solidity >=0.7.0 <0.9.0;

/**
 * @dev Stripped version of `@balancer-labs/v2-interfaces/contracts/vault/IVault.sol`
 * that only exposes the necessary vault methods for DepositTradeHelper.
 */
interface IVault {
  /**
   * @dev Returns a Pool's registered tokens, the total balance for each, and the latest block when *any* of
   * the tokens' `balances` changed.
   *
   * The order of the `tokens` array is the same order that will be used in `joinPool`, `exitPool`, as well as in all
   * Pool hooks (where applicable). Calls to `registerTokens` and `deregisterTokens` may change this order.
   *
   * If a Pool only registers tokens once, and these are sorted in ascending order, they will be stored in the same
   * order as passed to `registerTokens`.
   *
   * Total balances include both tokens held by the Vault and those withdrawn by the Pool's Asset Managers. These are
   * the amounts used by joins, exits and swaps. For a detailed breakdown of token balances, use `getPoolTokenInfo`
   * instead.
   */
  function getPoolTokens(bytes32 poolId)
    external
    view
    returns (
      IERC20[] memory tokens,
      uint256[] memory balances,
      uint256 lastChangeBlock
    );

  // Swaps
  //
  // Users can swap tokens with Pools by calling the `swap` and `batchSwap` functions. To do this,
  // they need not trust Pool contracts in any way: all security checks are made by the Vault. They must however be
  // aware of the Pools' pricing algorithms in order to estimate the prices Pools will quote.
  //
  // The `swap` function executes a single swap, while `batchSwap` can perform multiple swaps in sequence.
  // In each individual swap, tokens of one kind are sent from the sender to the Pool (this is the 'token in'),
  // and tokens of another kind are sent from the Pool to the recipient in exchange (this is the 'token out').
  // More complex swaps, such as one token in to multiple tokens out can be achieved by batching together
  // individual swaps.
  //
  // There are two swap kinds:
  //  - 'given in' swaps, where the amount of tokens in (sent to the Pool) is known, and the Pool determines (via the
  // `onSwap` hook) the amount of tokens out (to send to the recipient).
  //  - 'given out' swaps, where the amount of tokens out (received from the Pool) is known, and the Pool determines
  // (via the `onSwap` hook) the amount of tokens in (to receive from the sender).
  //
  // Additionally, it is possible to chain swaps using a placeholder input amount, which the Vault replaces with
  // the calculated output of the previous swap. If the previous swap was 'given in', this will be the calculated
  // tokenOut amount. If the previous swap was 'given out', it will use the calculated tokenIn amount. These extended
  // swaps are known as 'multihop' swaps, since they 'hop' through a number of intermediate tokens before arriving at
  // the final intended token.
  //
  // In all cases, tokens are only transferred in and out of the Vault (or withdrawn from and deposited into Internal
  // Balance) after all individual swaps have been completed, and the net token balance change computed. This makes
  // certain swap patterns, such as multihops, or swaps that interact with the same token pair in multiple Pools, cost
  // much less gas than they would otherwise.
  //
  // It also means that under certain conditions it is possible to perform arbitrage by swapping with multiple
  // Pools in a way that results in net token movement out of the Vault (profit), with no tokens being sent in (only
  // updating the Pool's internal accounting).
  //
  // To protect users from front-running or the market changing rapidly, they supply a list of 'limits' for each token
  // involved in the swap, where either the maximum number of tokens to send (by passing a positive value) or the
  // minimum amount of tokens to receive (by passing a negative value) is specified.
  //
  // Additionally, a 'deadline' timestamp can also be provided, forcing the swap to fail if it occurs after
  // this point in time (e.g. if the transaction failed to be included in a block promptly).
  //
  // If interacting with Pools that hold WETH, it is possible to both send and receive ETH directly: the Vault will do
  // the wrapping and unwrapping. To enable this mechanism, the IAsset sentinel value (the zero address) must be
  // passed in the `assets` array instead of the WETH address. Note that it is possible to combine ETH and WETH in the
  // same swap. Any excess ETH will be sent back to the caller (not the sender, which is relevant for relayers).
  //
  // Finally, Internal Balance can be used when either sending or receiving tokens.

  enum SwapKind {
    GIVEN_IN,
    GIVEN_OUT
  }

  /**
   * @dev Performs a swap with a single Pool.
   *
   * If the swap is 'given in' (the number of tokens to send to the Pool is known), it returns the amount of tokens
   * taken from the Pool, which must be greater than or equal to `limit`.
   *
   * If the swap is 'given out' (the number of tokens to take from the Pool is known), it returns the amount of tokens
   * sent to the Pool, which must be less than or equal to `limit`.
   *
   * Internal Balance usage and the recipient are determined by the `funds` struct.
   *
   * Emits a `Swap` event.
   */
  function swap(
    SingleSwap memory singleSwap,
    FundManagement memory funds,
    uint256 limit,
    uint256 deadline
  ) external payable returns (uint256);

  /**
   * @dev Data for a single swap executed by `swap`. `amount` is either `amountIn` or `amountOut` depending on
   * the `kind` value.
   *
   * `assetIn` and `assetOut` are either token addresses, or the IAsset sentinel value for ETH (the zero address).
   * Note that Pools never interact with ETH directly: it will be wrapped to or unwrapped from WETH by the Vault.
   *
   * The `userData` field is ignored by the Vault, but forwarded to the Pool in the `onSwap` hook, and may be
   * used to extend swap behavior.
   */
  struct SingleSwap {
    bytes32 poolId;
    SwapKind kind;
    IAsset assetIn;
    IAsset assetOut;
    uint256 amount;
    bytes userData;
  }

  /**
   * @dev Data for each individual swap executed by `batchSwap`. The asset in and out fields are indexes into the
   * `assets` array passed to that function, and ETH assets are converted to WETH.
   *
   * If `amount` is zero, the multihop mechanism is used to determine the actual amount based on the amount in/out
   * from the previous swap, depending on the swap kind.
   *
   * The `userData` field is ignored by the Vault, but forwarded to the Pool in the `onSwap` hook, and may be
   * used to extend swap behavior.
   */
  struct BatchSwapStep {
    bytes32 poolId;
    uint256 assetInIndex;
    uint256 assetOutIndex;
    uint256 amount;
    bytes userData;
  }

  /**
   * @dev All tokens in a swap are either sent from the `sender` account to the Vault, or from the Vault to the
   * `recipient` account.
   *
   * If the caller is not `sender`, it must be an authorized relayer for them.
   *
   * If `fromInternalBalance` is true, the `sender`'s Internal Balance will be preferred, performing an ERC20
   * transfer for the difference between the requested amount and the User's Internal Balance (if any). The `sender`
   * must have allowed the Vault to use their tokens via `IERC20.approve()`. This matches the behavior of
   * `joinPool`.
   *
   * If `toInternalBalance` is true, tokens will be deposited to `recipient`'s internal balance instead of
   * transferred. This matches the behavior of `exitPool`.
   *
   * Note that ETH cannot be deposited to or withdrawn from Internal Balance: attempting to do so will trigger a
   * revert.
   */
  struct FundManagement {
    address sender;
    bool fromInternalBalance;
    address payable recipient;
    bool toInternalBalance;
  }

  /**
   * @dev Simulates a call to `batchSwap`, returning an array of Vault asset deltas. Calls to `swap` cannot be
   * simulated directly, but an equivalent `batchSwap` call can and will yield the exact same result.
   *
   * Each element in the array corresponds to the asset at the same index, and indicates the number of tokens (or ETH)
   * the Vault would take from the sender (if positive) or send to the recipient (if negative). The arguments it
   * receives are the same that an equivalent `batchSwap` call would receive.
   *
   * Unlike `batchSwap`, this function performs no checks on the sender or recipient field in the `funds` struct.
   * This makes it suitable to be called by off-chain applications via eth_call without needing to hold tokens,
   * approve them for the Vault, or even know a user's address.
   *
   * Note that this function is not 'view' (due to implementation details): the client code must explicitly execute
   * eth_call instead of eth_sendTransaction.
   */
  function queryBatchSwap(
    SwapKind kind,
    BatchSwapStep[] memory swaps,
    IAsset[] memory assets,
    FundManagement memory funds
  ) external returns (int256[] memory assetDeltas);
}

contracts/Collateral.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity =0.8.7;

import "./interfaces/ICollateral.sol";
import "./interfaces/IHook.sol";
import "prepo-shared-contracts/contracts/SafeAccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/draft-ERC20PermitUpgradeable.sol";

contract Collateral is
  ICollateral,
  ERC20PermitUpgradeable,
  ReentrancyGuardUpgradeable,
  SafeAccessControlEnumerableUpgradeable
{
  IERC20 private immutable _baseToken;
  uint256 private immutable _baseTokenDenominator;
  uint256 private _depositFee;
  uint256 private _withdrawFee;
  IHook private _depositHook;
  IHook private _withdrawHook;

  uint256 public constant override PERCENT_DENOMINATOR = 1000000;
  uint256 public constant override FEE_LIMIT = 100000;
  bytes32 public constant override SET_DEPOSIT_FEE_ROLE =
    keccak256("setDepositFee");
  bytes32 public constant override SET_WITHDRAW_FEE_ROLE =
    keccak256("setWithdrawFee");
  bytes32 public constant override SET_DEPOSIT_HOOK_ROLE =
    keccak256("setDepositHook");
  bytes32 public constant override SET_WITHDRAW_HOOK_ROLE =
    keccak256("setWithdrawHook");

  constructor(IERC20 baseToken, uint256 baseTokenDecimals) {
    _baseToken = baseToken;
    _baseTokenDenominator = 10**baseTokenDecimals;
  }

  function initialize(string memory name, string memory symbol)
    public
    initializer
  {
    __SafeAccessControlEnumerable_init();
    __ERC20_init(name, symbol);
    __ERC20Permit_init(name);
    __ReentrancyGuard_init();
  }

  /**
   * @dev If hook not set, fees remain within the contract as extra reserves
   * (withdrawable by manager). Converts amount after fee from base token
   * units to collateral token units.
   */
  function deposit(address recipient, uint256 baseTokenAmount)
    external
    override
    nonReentrant
    returns (uint256 collateralMintAmount)
  {
    uint256 fee = (baseTokenAmount * _depositFee) / PERCENT_DENOMINATOR;
    if (_depositFee > 0) {
      require(fee > 0, "fee = 0");
    } else {
      require(baseTokenAmount > 0, "base token amount = 0");
    }
    _baseToken.transferFrom(msg.sender, address(this), baseTokenAmount);
    uint256 baseTokenAmountAfterFee = baseTokenAmount - fee;
    if (address(_depositHook) != address(0)) {
      _baseToken.approve(address(_depositHook), fee);
      _depositHook.hook(
        msg.sender,
        recipient,
        baseTokenAmount,
        baseTokenAmountAfterFee
      );
      _baseToken.approve(address(_depositHook), 0);
    }
    collateralMintAmount =
      (baseTokenAmountAfterFee * 1e18) /
      _baseTokenDenominator;
    _mint(recipient, collateralMintAmount);
    emit Deposit(recipient, baseTokenAmountAfterFee, fee);
  }

  function withdraw(address recipient, uint256 collateralAmount)
    external
    override
    nonReentrant
    returns (uint256 baseTokenAmountAfterFee)
  {
    uint256 baseTokenAmount = (collateralAmount * _baseTokenDenominator) /
      1e18;
    uint256 fee = (baseTokenAmount * _withdrawFee) / PERCENT_DENOMINATOR;
    if (_withdrawFee > 0) {
      require(fee > 0, "fee = 0");
    } else {
      require(baseTokenAmount > 0, "base token amount = 0");
    }
    _burn(msg.sender, collateralAmount);
    baseTokenAmountAfterFee = baseTokenAmount - fee;
    if (address(_withdrawHook) != address(0)) {
      _baseToken.approve(address(_withdrawHook), fee);
      _withdrawHook.hook(
        msg.sender,
        recipient,
        baseTokenAmount,
        baseTokenAmountAfterFee
      );
      _baseToken.approve(address(_withdrawHook), 0);
    }
    _baseToken.transfer(recipient, baseTokenAmountAfterFee);
    emit Withdraw(msg.sender, recipient, baseTokenAmountAfterFee, fee);
  }

  function setDepositFee(uint256 depositFee)
    external
    override
    onlyRole(SET_DEPOSIT_FEE_ROLE)
  {
    require(depositFee <= FEE_LIMIT, "Exceeds fee limit");
    _depositFee = depositFee;
    emit DepositFeeChange(depositFee);
  }

  function setWithdrawFee(uint256 withdrawFee)
    external
    override
    onlyRole(SET_WITHDRAW_FEE_ROLE)
  {
    require(withdrawFee <= FEE_LIMIT, "Exceeds fee limit");
    _withdrawFee = withdrawFee;
    emit WithdrawFeeChange(withdrawFee);
  }

  function setDepositHook(IHook depositHook)
    external
    override
    onlyRole(SET_DEPOSIT_HOOK_ROLE)
  {
    _depositHook = depositHook;
    emit DepositHookChange(address(depositHook));
  }

  function setWithdrawHook(IHook withdrawHook)
    external
    override
    onlyRole(SET_WITHDRAW_HOOK_ROLE)
  {
    _withdrawHook = withdrawHook;
    emit WithdrawHookChange(address(withdrawHook));
  }

  function getBaseToken() external view override returns (IERC20) {
    return _baseToken;
  }

  function getDepositFee() external view override returns (uint256) {
    return _depositFee;
  }

  function getWithdrawFee() external view override returns (uint256) {
    return _withdrawFee;
  }

  function getDepositHook() external view override returns (IHook) {
    return _depositHook;
  }

  function getWithdrawHook() external view override returns (IHook) {
    return _withdrawHook;
  }

  function getBaseTokenBalance() external view override returns (uint256) {
    return _baseToken.balanceOf(address(this));
  }
}

contracts/DepositHook.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity =0.8.7;

import "./AllowedCollateralCaller.sol";
import "./DepositRecordCaller.sol";
import "./interfaces/IDepositHook.sol";
import "prepo-shared-contracts/contracts/SafeAccessControlEnumerable.sol";
import "prepo-shared-contracts/contracts/TokenSenderCaller.sol";
import "prepo-shared-contracts/contracts/TreasuryCaller.sol";

contract DepositHook is
  IDepositHook,
  AllowedCollateralCaller,
  DepositRecordCaller,
  SafeAccessControlEnumerable,
  TokenSenderCaller,
  TreasuryCaller
{
  bool private _depositsAllowed;

  bytes32 public constant override SET_COLLATERAL_ROLE =
    keccak256("setCollateral");
  bytes32 public constant override SET_DEPOSIT_RECORD_ROLE =
    keccak256("setDepositRecord");
  bytes32 public constant override SET_DEPOSITS_ALLOWED_ROLE =
    keccak256("setDepositsAllowed");
  bytes32 public constant override SET_TREASURY_ROLE =
    keccak256("setTreasury");
  bytes32 public constant override SET_TOKEN_SENDER_ROLE =
    keccak256("setTokenSender");

  function hook(
    address,
    address recipient,
    uint256 amountBeforeFee,
    uint256 amountAfterFee
  ) external override onlyCollateral {
    require(_depositsAllowed, "Deposits not allowed");
    _depositRecord.recordDeposit(recipient, amountAfterFee);
    uint256 fee = amountBeforeFee - amountAfterFee;
    if (fee > 0) {
      _collateral.getBaseToken().transferFrom(
        address(_collateral),
        _treasury,
        fee
      );
      _tokenSender.send(recipient, fee);
    }
  }

  function setCollateral(ICollateral collateral)
    public
    override
    onlyRole(SET_COLLATERAL_ROLE)
  {
    super.setCollateral(collateral);
  }

  function setDepositRecord(IDepositRecord depositRecord)
    public
    override
    onlyRole(SET_DEPOSIT_RECORD_ROLE)
  {
    super.setDepositRecord(depositRecord);
  }

  function setDepositsAllowed(bool depositsAllowed)
    external
    override
    onlyRole(SET_DEPOSITS_ALLOWED_ROLE)
  {
    _depositsAllowed = depositsAllowed;
    emit DepositsAllowedChange(depositsAllowed);
  }

  function setTreasury(address treasury)
    public
    override
    onlyRole(SET_TREASURY_ROLE)
  {
    super.setTreasury(treasury);
  }

  function setTokenSender(ITokenSender tokenSender)
    public
    override
    onlyRole(SET_TOKEN_SENDER_ROLE)
  {
    super.setTokenSender(tokenSender);
  }

  function depositsAllowed() external view override returns (bool) {
    return _depositsAllowed;
  }
}

contracts/DepositRecord.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity =0.8.7;

import "./interfaces/IDepositRecord.sol";
import "prepo-shared-contracts/contracts/AccountListCaller.sol";
import "prepo-shared-contracts/contracts/AllowedMsgSenders.sol";
import "prepo-shared-contracts/contracts/SafeAccessControlEnumerable.sol";

contract DepositRecord is
  IDepositRecord,
  AccountListCaller,
  AllowedMsgSenders,
  SafeAccessControlEnumerable
{
  uint256 private _globalNetDepositCap;
  uint256 private _globalNetDepositAmount;
  uint256 private _userDepositCap;
  mapping(address => uint256) private _userToDeposits;

  bytes32 public constant override SET_GLOBAL_NET_DEPOSIT_CAP_ROLE =
    keccak256("setGlobalNetDepositCap");
  bytes32 public constant override SET_USER_DEPOSIT_CAP_ROLE =
    keccak256("setUserDepositCap");
  bytes32 public constant override SET_ALLOWED_MSG_SENDERS_ROLE =
    keccak256("setAllowedMsgSenders");
  bytes32 public constant override SET_ACCOUNT_LIST_ROLE =
    keccak256("setAccountList");

  function recordDeposit(address user, uint256 amount)
    external
    override
    onlyAllowedMsgSenders
  {
    require(
      amount + _globalNetDepositAmount <= _globalNetDepositCap,
      "Global deposit cap exceeded"
    );
    if (!_accountList.isIncluded(user)) {
      require(
        amount + _userToDeposits[user] <= _userDepositCap,
        "User deposit cap exceeded"
      );
    }
    _globalNetDepositAmount += amount;
    _userToDeposits[user] += amount;
  }

  function recordWithdrawal(uint256 amount)
    external
    override
    onlyAllowedMsgSenders
  {
    if (_globalNetDepositAmount > amount) {
      _globalNetDepositAmount -= amount;
    } else {
      _globalNetDepositAmount = 0;
    }
  }

  function setGlobalNetDepositCap(uint256 globalNetDepositCap)
    external
    override
    onlyRole(SET_GLOBAL_NET_DEPOSIT_CAP_ROLE)
  {
    _globalNetDepositCap = globalNetDepositCap;
    emit GlobalNetDepositCapChange(globalNetDepositCap);
  }

  function setUserDepositCap(uint256 userDepositCap)
    external
    override
    onlyRole(SET_USER_DEPOSIT_CAP_ROLE)
  {
    _userDepositCap = userDepositCap;
    emit UserDepositCapChange(userDepositCap);
  }

  function setAllowedMsgSenders(IAccountList allowedMsgSenders)
    public
    virtual
    override
    onlyRole(SET_ALLOWED_MSG_SENDERS_ROLE)
  {
    super.setAllowedMsgSenders(allowedMsgSenders);
  }

  function setAccountList(IAccountList accountList)
    public
    virtual
    override
    onlyRole(SET_ACCOUNT_LIST_ROLE)
  {
    super.setAccountList(accountList);
  }

  function getGlobalNetDepositCap() external view override returns (uint256) {
    return _globalNetDepositCap;
  }

  function getGlobalNetDepositAmount()
    external
    view
    override
    returns (uint256)
  {
    return _globalNetDepositAmount;
  }

  function getUserDepositCap() external view override returns (uint256) {
    return _userDepositCap;
  }

  function getUserDepositAmount(address account)
    external
    view
    override
    returns (uint256)
  {
    return _userToDeposits[account];
  }
}

